// Dino :)
Dino a;

int main() {
    // Clear background
    LCD.SetBackgroundColor(BLACK);
    LCD.Clear();

    // Testing Dino class
    a.setX(50.0);
    a.setY(100.0);
    a.setWidth(20);
    a.setHeight(20);
    
    a.Draw();
    while (1) {
        for (int i = 0; i < TREX_HEIGHT; ++i) {
            for (int j = 0; j < TREX_WIDTH; ++j) {
                int col = t_rex_title[i * TREX_WIDTH + j];
                LCD.SetFontColor(col ? WHITE : BLACK);
                LCD.DrawPixel(j, i);
            }
        }
        LCD.Update();
        Sleep(1 / FPS);
        UpdateFrame();
        // Never end
    }
    return 0;
}

void UpdateFrame(){
    UpdateDinosaur();
    UpdateObstacles();
    //TODO
}

void UpdateDinosaur(){
    a.Erase();
    a.UpdateVelocity();
    a.UpdatePosition();
    a.Draw();
}

void UpdateObstacles(){
    //TODO
}